input.csh:
set do_nudge = .true. cd $workDir
if ( -f time_stamp.out_sv ) then if ( -r time_stamp.out_sv ) then mv -f time_stamp.out_sv time_stamp.out endif endif
# the begin time of this segment is the end time of the last segment (`timeStamp -e`) set begindate = `timeStamp -e` if ( $begindate == 'no_time_stamp' ) then set year_num = `echo $baseDate | cut -c -4` set month_num = `echo $baseDate | cut -c 5-7` if ( $month_num < 10 ) then set month_num = `printf "%.1d"${month_num}` set day_num = `echo $baseDate | cut -c 8-9` else set day_num = `echo $baseDate | cut -c 8-10` endif # if ( $month_num < 10 ) if ( $day_num < 10 ) set day_num = `printf "%.1d"${day_num}` set begindate = ${year_num}${month_num}${day_num}
endif if ( -f time_stamp.out ) rm -f time_stamp.out
echo 'begin date is '$begindate
# set the first analysis data file to be used set currentdate = $begindate set file_prefix = "NCEP_"$currentdate set analysis_first = $file_prefix{00.nc}
